var _ resourceids.ResourceId = &AutoUpgradeProfileId{}

func TestNewAutoUpgradeProfileID(t *testing.T) {
	id := NewAutoUpgradeProfileID("12345678-1234-9876-4563-123456789012", "example-resource-group", "fleetName", "autoUpgradeProfileName")

	if id.SubscriptionId != "12345678-1234-9876-4563-123456789012" {
		t.Fatalf("Expected %q but got %q for Segment 'SubscriptionId'", id.SubscriptionId, "12345678-1234-9876-4563-123456789012")
	}

	if id.ResourceGroupName != "example-resource-group" {
		t.Fatalf("Expected %q but got %q for Segment 'ResourceGroupName'", id.ResourceGroupName, "example-resource-group")
	}

	if id.FleetName != "fleetName" {
		t.Fatalf("Expected %q but got %q for Segment 'FleetName'", id.FleetName, "fleetName")
	}

	if id.AutoUpgradeProfileName != "autoUpgradeProfileName" {
		t.Fatalf("Expected %q but got %q for Segment 'AutoUpgradeProfileName'", id.AutoUpgradeProfileName, "autoUpgradeProfileName")
	}
}

func TestFormatAutoUpgradeProfileID(t *testing.T) {
	actual := NewAutoUpgradeProfileID("12345678-1234-9876-4563-123456789012", "example-resource-group", "fleetName", "autoUpgradeProfileName").ID()
	expected := "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService/fleets/fleetName/autoUpgradeProfiles/autoUpgradeProfileName"
	if actual != expected {
		t.Fatalf("Expected the Formatted ID to be %q but got %q", expected, actual)
	}
}

func TestParseAutoUpgradeProfileID(t *testing.T) {
	testData := []struct {
		Input    string
		Error    bool
		Expected *AutoUpgradeProfileId
	}{
		{
			// Incomplete URI
			Input: "",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService/fleets",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService/fleets/fleetName",
			Error: true,
		},
		{
			// Incomplete URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService/fleets/fleetName/autoUpgradeProfiles",
			Error: true,
		},
		{
			// Valid URI
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService/fleets/fleetName/autoUpgradeProfiles/autoUpgradeProfileName",
			Expected: &AutoUpgradeProfileId{
				SubscriptionId:         "12345678-1234-9876-4563-123456789012",
				ResourceGroupName:      "example-resource-group",
				FleetName:              "fleetName",
				AutoUpgradeProfileName: "autoUpgradeProfileName",
			},
		},
		{
			// Invalid (Valid Uri with Extra segment)
			Input: "/subscriptions/12345678-1234-9876-4563-123456789012/resourceGroups/example-resource-group/providers/Microsoft.ContainerService/fleets/fleetName/autoUpgradeProfiles/autoUpgradeProfileName/extra",
			Error: true,
		},
	}
	for _, v := range testData {
		t.Logf("[DEBUG] Testing %q", v.Input)

		actual, err := ParseAutoUpgradeProfileID(v.Input)
		if err != nil {
			if v.Error {
				continue
			}

			t.Fatalf("Expect a value but got an error: %+v", err)
		}
		if v.Error {
			t.Fatal("Expect an error but didn't get one")
		}

		if actual.SubscriptionId != v.Expected.SubscriptionId {
			t.Fatalf("Expected %q but got %q for SubscriptionId", v.Expected.SubscriptionId, actual.SubscriptionId)
		}

		if actual.ResourceGroupName != v.Expected.ResourceGroupName {
			t.Fatalf("Expected %q but got %q for ResourceGroupName", v.Expected.ResourceGroupName, actual.ResourceGroupName)
		}

		if actual.FleetName != v.Expected.FleetName {
			t.Fatalf("Expected %q but got %q for FleetName", v.Expected.FleetName, actual.FleetName)
		}

		if actual.AutoUpgradeProfileName != v.Expected.AutoUpgradeProfileName {
			t.Fatalf("Expected %q but got %q for AutoUpgradeProfileName", v.Expected.AutoUpgradeProfileName, actual.AutoUpgradeProfileName)
		}

	}
}

