type NetworkProtocol string

const (
	NetworkProtocolAny  NetworkProtocol = "Any"
	NetworkProtocolICMP NetworkProtocol = "ICMP"
	NetworkProtocolTCP  NetworkProtocol = "TCP"
	NetworkProtocolUDP  NetworkProtocol = "UDP"
)

func PossibleValuesForNetworkProtocol() []string {
	return []string{
		string(NetworkProtocolAny),
		string(NetworkProtocolICMP),
		string(NetworkProtocolTCP),
		string(NetworkProtocolUDP),
	}
}

func (s *NetworkProtocol) UnmarshalJSON(bytes []byte) error {
	var decoded string
	if err := json.Unmarshal(bytes, &decoded); err != nil {
		return fmt.Errorf("unmarshaling: %+v", err)
	}
	out, err := parseNetworkProtocol(decoded)
	if err != nil {
		return fmt.Errorf("parsing %q: %+v", decoded, err)
	}
	*s = *out
	return nil
}

func parseNetworkProtocol(input string) (*NetworkProtocol, error) {
	vals := map[string]NetworkProtocol{
		"any":  NetworkProtocolAny,
		"icmp": NetworkProtocolICMP,
		"tcp":  NetworkProtocolTCP,
		"udp":  NetworkProtocolUDP,
	}
	if v, ok := vals[strings.ToLower(input)]; ok {
		return &v, nil
	}

	// otherwise presume it's an undefined value and best-effort it
	out := NetworkProtocol(input)
	return &out, nil
}

